export type PriceTier = '$' | '$$' | '$$$' | '$$$$'

export type DayOfWeek = 'mon' | 'tue' | 'wed' | 'thu' | 'fri' | 'sat' | 'sun'

// [open, close] in 24h format e.g. ['06:00', '22:00'], or null = closed that day
export type DayHours = [string, string] | null

export type WeeklyHours = Record<DayOfWeek, DayHours>

export type CuisineTag = 'japanese' | 'italian' | 'mexican' | 'seafood' | 'american' | 'french' | 'californian' | 'health' | 'brunch'

export interface Competitor {
  name: string
  lat: number
  lng: number
  rating: number | null
  reviewCount: number | null
  priceTier: PriceTier | null
  note: string
  onSite: boolean
  category: CategoryId
  googleMapsUrl: string | null
  website: string | null
  hours: WeeklyHours | null
  cuisineTag?: CuisineTag
}

export type SuperCategoryId = 'fitness' | 'dining' | 'wellness' | 'social' | 'work' | 'outdoors'

export type CategoryId =
  // Fitness & Sports
  | 'gyms'
  | 'yoga'
  | 'tennis'
  | 'golf'
  // Dining
  | 'dining'
  | 'upscale_casual'
  | 'sushi'
  | 'casual'
  | 'health_food'
  | 'brunch'
  // Wellness & Beauty
  | 'spa'
  | 'juice'
  | 'beauty'
  // Social & Nightlife
  | 'bars'
  | 'wine'
  | 'entertainment'
  // Work & Lifestyle
  | 'cowork'
  | 'shopping'
  // Outdoors & Water
  | 'pools'
  | 'water_sports'

export interface CategoryInfo {
  id: CategoryId
  label: string
  color: string
  hyattHas: string
  gap: string
}

export interface SuperCategoryInfo {
  id: SuperCategoryId
  label: string
  color: string
  children: CategoryInfo[]
}

